// ===== PROCESADOR PRINCIPAL DE MENSAJES CON ESTADOS =====
const data = $input.all()[0].json;

console.log('Datos recibidos:', JSON.stringify(data, null, 2));

// Extraer informaci√≥n del mensaje
let messageText = '';
let userNumber = '';
let userName = 'Usuario';

try {
  if (data.body && data.body.data) {
    messageText = data.body.data.message?.conversation || 
                  data.body.data.message?.extendedTextMessage?.text || '';
    userNumber = data.body.data.key?.remoteJid?.replace('@s.whatsapp.net', '') || '';
    userName = data.body.data.pushName || 'Usuario';
  } else if (data.data) {
    messageText = data.data.message?.conversation || 
                  data.data.message?.extendedTextMessage?.text || '';
    userNumber = data.data.key?.remoteJid?.replace('@s.whatsapp.net', '') || '';
    userName = data.data.pushName || 'Usuario';
  } else {
    messageText = 'hola';
    userNumber = '5493735507036';
  }
} catch (error) {
  console.log('Error procesando datos:', error);
  messageText = 'hola';
  userNumber = '5493735507036';
  userName = 'Usuario';
}

const normalizedMessage = messageText.toLowerCase().trim();

// ===== SISTEMA DE ESTADOS =====
// Simulamos un estado por usuario (en producci√≥n esto deber√≠a estar en una DB)
const userStates = $workflow.static.userStates || {};
const currentState = userStates[userNumber] || { state: 'menu', data: {} };

let responseData = {
  userNumber,
  userName,
  messageText,
  normalizedMessage,
  currentState: currentState.state,
  stateData: currentState.data,
  action: 'send_message', // default action
  needsAPICall: false,
  apiEndpoint: '',
  apiData: {}
};

// ===== L√ìGICA DE ESTADOS =====
switch (currentState.state) {
  case 'menu':
  case 'initial':
    if (normalizedMessage.includes('hola') || normalizedMessage.includes('hi') || normalizedMessage === '') {
      responseData.responseText = `ü§ñ ¬°Hola ${userName}! üëã\n\nSoy Anita, tu asistente m√©dica virtual.\n\nüìã ¬øEn qu√© puedo ayudarte?\n\nüü¢ Escribe *1* para agendar una cita\nüü¢ Escribe *2* para ver horarios disponibles\nüü¢ Escribe *3* para informaci√≥n de contacto\nüü¢ Escribe *menu* para volver aqu√≠`;
      currentState.state = 'waiting_menu_option';
    } else if (normalizedMessage.includes('1') || normalizedMessage.includes('cita') || normalizedMessage.includes('agendar')) {
      responseData.responseText = `üìÖ *Perfecto! Vamos a agendar tu cita* ‚úÖ\n\nPara comenzar, necesito algunos datos:\n\nüë§ **Paso 1 de 5: Nombre completo**\n\nPor favor escribe tu nombre completo:`;
      currentState.state = 'collecting_name';
      currentState.data = {};
    } else if (normalizedMessage.includes('2') || normalizedMessage.includes('horarios')) {
      responseData.responseText = `üìÖ *Consulta de Horarios Disponibles*\n\nPor favor env√≠ame la fecha en formato DD/MM/AAAA\n\nEjemplo: 15/12/2024\n\nüí° Tip: Solo consulto horarios de lunes a s√°bado`;
      currentState.state = 'asking_date_for_availability';
    } else if (normalizedMessage.includes('3') || normalizedMessage.includes('contacto')) {
      responseData.responseText = `üìû *Informaci√≥n de Contacto*\n\nüè• **Consultorio M√©dico**\nüìç Direcci√≥n: [Tu direcci√≥n aqu√≠]\n‚òéÔ∏è Tel√©fono: [Tu tel√©fono]\nüïí Horarios de Atenci√≥n:\n\nüìÖ **Lunes a Viernes:**\nüåÖ Ma√±ana: 10:00 - 12:00\nüåÜ Tarde: 17:00 - 20:00\n\nüìÖ **S√°bados:**\nüåÖ Ma√±ana: 10:00 - 12:00\n\n‚ùå **Domingos:** Cerrado\n\nüíª Web: https://micitamedica.me\nüì± WhatsApp: Este n√∫mero\n\nEscribe *menu* para volver al men√∫ principal`;
      currentState.state = 'waiting_menu_option';
    } else {
      responseData.responseText = `ü§î No entend√≠ tu mensaje: "${messageText}"\n\nüìã Opciones disponibles:\n\nüü¢ Escribe *1* para agendar una cita\nüü¢ Escribe *2* para ver horarios\nüü¢ Escribe *3* para informaci√≥n de contacto\nüü¢ Escribe *menu* para el men√∫ principal`;
      currentState.state = 'waiting_menu_option';
    }
    break;

  case 'waiting_menu_option':
    if (normalizedMessage.includes('1') || normalizedMessage.includes('cita')) {
      responseData.responseText = `üìÖ *Perfecto! Vamos a agendar tu cita* ‚úÖ\n\nPara comenzar, necesito algunos datos:\n\nüë§ **Paso 1 de 5: Nombre completo**\n\nPor favor escribe tu nombre completo:`;
      currentState.state = 'collecting_name';
      currentState.data = {};
    } else if (normalizedMessage.includes('2') || normalizedMessage.includes('horarios')) {
      responseData.responseText = `üìÖ *Consulta de Horarios Disponibles*\n\nPor favor env√≠ame la fecha en formato DD/MM/AAAA\n\nEjemplo: 15/12/2024`;
      currentState.state = 'asking_date_for_availability';
    } else if (normalizedMessage.includes('3') || normalizedMessage.includes('contacto')) {
      responseData.responseText = `üìû *Informaci√≥n de Contacto*\n\nüè• **Consultorio M√©dico**\nüìç Direcci√≥n: [Tu direcci√≥n]\n‚òéÔ∏è Tel√©fono: [Tu tel√©fono]\nüïí Horarios: Lunes a Viernes 10:00-12:00, 17:00-20:00\nS√°bados: 10:00-12:00\n\nüíª Web: https://micitamedica.me\n\nEscribe *menu* para volver al men√∫ principal`;
      currentState.state = 'waiting_menu_option';
    } else if (normalizedMessage.includes('menu')) {
      responseData.responseText = `üìã *Men√∫ Principal*\n\nüü¢ Escribe *1* para agendar una cita\nüü¢ Escribe *2* para ver horarios disponibles\nüü¢ Escribe *3* para informaci√≥n de contacto`;
      currentState.state = 'waiting_menu_option';
    } else {
      responseData.responseText = `ü§î Opci√≥n no v√°lida.\n\nüìã Opciones disponibles:\n\nüü¢ *1* - Agendar cita\nüü¢ *2* - Ver horarios\nüü¢ *3* - Contacto\nüü¢ *menu* - Men√∫ principal`;
    }
    break;

  case 'asking_date_for_availability':
    // Validar formato de fecha DD/MM/AAAA
    const dateMatch = messageText.match(/(\d{1,2})\/(\d{1,2})\/(\d{4})/);
    if (dateMatch) {
      const [, day, month, year] = dateMatch;
      const requestedDate = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;

      responseData.needsAPICall = true;
      responseData.action = 'get_available_times';
      responseData.apiEndpoint = `/api/appointments/available/${requestedDate}`;
      responseData.requestedDate = requestedDate;
      responseData.displayDate = `${day}/${month}/${year}`;
      currentState.state = 'waiting_menu_option';
    } else {
      responseData.responseText = `‚ùå Formato de fecha incorrecto.\n\nPor favor usa el formato: DD/MM/AAAA\n\nEjemplo: 15/12/2024\n\nIntenta nuevamente:`;
    }
    break;

  case 'collecting_name':
    if (messageText.trim().length >= 3) {
      currentState.data.clientName = messageText.trim();
      responseData.responseText = `‚úÖ Nombre: ${messageText.trim()}\n\nüì± **Paso 2 de 5: Tel√©fono**\n\nPor favor escribe tu n√∫mero de tel√©fono\n(puede ser el mismo desde donde escribes):`;
      currentState.state = 'collecting_phone';
    } else {
      responseData.responseText = `‚ùå El nombre debe tener al menos 3 caracteres.\n\nPor favor escribe tu nombre completo:`;
    }
    break;

  case 'collecting_phone':
    const phonePattern = /^[\d\s\-\+\(\)]{7,}$/;
    if (phonePattern.test(messageText.trim()) || messageText.trim().length >= 7) {
      currentState.data.phone = messageText.trim();
      responseData.responseText = `‚úÖ Tel√©fono: ${messageText.trim()}\n\nüè• **Paso 3 de 5: Obra Social**\n\nSelecciona tu obra social:\n\n1Ô∏è‚É£ INSSSEP\n2Ô∏è‚É£ Swiss Medical\n3Ô∏è‚É£ OSDE\n4Ô∏è‚É£ Galeno\n5Ô∏è‚É£ Consulta Particular\n6Ô∏è‚É£ Otras Obras Sociales\n\nEscribe el n√∫mero de tu opci√≥n:`;
      currentState.state = 'collecting_social_work';
    } else {
      responseData.responseText = `‚ùå Formato de tel√©fono incorrecto.\n\nPor favor escribe un n√∫mero de tel√©fono v√°lido:`;
    }
    break;

  case 'collecting_social_work':
    const socialWorkOptions = {
      '1': 'INSSSEP',
      '2': 'Swiss Medical', 
      '3': 'OSDE',
      '4': 'Galeno',
      '5': 'CONSULTA PARTICULAR',
      '6': 'Otras Obras Sociales'
    };

    if (socialWorkOptions[normalizedMessage]) {
      currentState.data.socialWork = socialWorkOptions[normalizedMessage];
      responseData.responseText = `‚úÖ Obra Social: ${socialWorkOptions[normalizedMessage]}\n\nüìß **Paso 4 de 5: Email (opcional)**\n\nEscribe tu email o env√≠a "sin email" para continuar:`;
      currentState.state = 'collecting_email';
    } else {
      responseData.responseText = `‚ùå Opci√≥n no v√°lida.\n\nPor favor selecciona un n√∫mero del 1 al 6:\n\n1Ô∏è‚É£ INSSSEP\n2Ô∏è‚É£ Swiss Medical\n3Ô∏è‚É£ OSDE\n4Ô∏è‚É£ Galeno\n5Ô∏è‚É£ Consulta Particular\n6Ô∏è‚É£ Otras Obras Sociales`;
    }
    break;

  case 'collecting_email':
    if (normalizedMessage.includes('sin email') || normalizedMessage.includes('no')) {
      currentState.data.email = '';
      responseData.responseText = `‚úÖ Email: No proporcionado\n\nüìÖ **Paso 5 de 5: Seleccionar fecha**\n\nEscribe la fecha deseada en formato DD/MM/AAAA\n\nEjemplo: 15/12/2024\n\nüí° Atenci√≥n de lunes a s√°bado`;
      currentState.state = 'collecting_date';
    } else {
      const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (emailPattern.test(messageText.trim())) {
        currentState.data.email = messageText.trim();
        responseData.responseText = `‚úÖ Email: ${messageText.trim()}\n\nüìÖ **Paso 5 de 5: Seleccionar fecha**\n\nEscribe la fecha deseada en formato DD/MM/AAAA\n\nEjemplo: 15/12/2024\n\nüí° Atenci√≥n de lunes a s√°bado`;
        currentState.state = 'collecting_date';
      } else {
        responseData.responseText = `‚ùå Email no v√°lido.\n\nEjemplo: usuario@email.com\n\nO escribe "sin email" para continuar:`;
      }
    }
    break;

  case 'collecting_date':
    const appointmentDateMatch = messageText.match(/(\d{1,2})\/(\d{1,2})\/(\d{4})/);
    if (appointmentDateMatch) {
      const [, day, month, year] = appointmentDateMatch;
      const appointmentDate = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;

      // Validar que la fecha no sea en el pasado
      const today = new Date();
      const selectedDate = new Date(appointmentDate);

      if (selectedDate < today) {
        responseData.responseText = `‚ùå No puedes agendar citas en fechas pasadas.\n\nPor favor selecciona una fecha futura (DD/MM/AAAA):`;
        break;
      }

      currentState.data.date = appointmentDate;
      currentState.data.displayDate = `${day}/${month}/${year}`;

      responseData.needsAPICall = true;
      responseData.action = 'get_available_times_for_booking';
      responseData.apiEndpoint = `/api/appointments/available/${appointmentDate}`;
      responseData.requestedDate = appointmentDate;
      currentState.state = 'selecting_time';
    } else {
      responseData.responseText = `‚ùå Formato de fecha incorrecto.\n\nUsa el formato: DD/MM/AAAA\nEjemplo: 15/12/2024\n\nIntenta nuevamente:`;
    }
    break;

  case 'selecting_time':
    // El usuario seleccion√≥ un horario
    if (currentState.data.availableTimes && currentState.data.availableTimes.includes(messageText.trim())) {
      currentState.data.time = messageText.trim();

      // Preparar datos para crear la cita
      responseData.needsAPICall = true;
      responseData.action = 'create_appointment';
      responseData.apiEndpoint = '/api/appointments';
      responseData.apiData = {
        clientName: currentState.data.clientName,
        phone: currentState.data.phone,
        socialWork: currentState.data.socialWork,
        email: currentState.data.email || '',
        date: currentState.data.date,
        time: currentState.data.time,
        isSobreturno: false
      };

      currentState.state = 'appointment_created';
    } else {
      responseData.responseText = `‚ùå Horario no v√°lido o no disponible.\n\nPor favor selecciona uno de los horarios mostrados anteriormente, copiando exactamente el formato (ej: 10:00)`;
    }
    break;

  default:
    responseData.responseText = `ü§î Ha ocurrido un error con el estado de la conversaci√≥n.\n\nEscribe *menu* para volver al men√∫ principal.`;
    currentState.state = 'waiting_menu_option';
}

// Guardar el estado actualizado
if (!$workflow.static.userStates) {
  $workflow.static.userStates = {};
}
$workflow.static.userStates[userNumber] = currentState;

return { json: responseData };

// Procesar respuesta de la API y generar mensaje apropiado
const inputData = $input.all()[0].json;
const apiResponse = $input.all()[1].json;

console.log('Procesando respuesta API:', JSON.stringify(apiResponse, null, 2));
console.log('Datos del procesador:', JSON.stringify(inputData, null, 2));

let responseText = '';
let updatedStateData = inputData.stateData || {};

// Procesar seg√∫n el tipo de acci√≥n
switch (inputData.action) {
  case 'get_available_times':
  case 'get_available_times_for_booking':
    if (apiResponse.success && apiResponse.data) {
      const { morning, afternoon } = apiResponse.data.available;

      // Filtrar solo horarios disponibles
      const availableMorning = morning.filter(slot => slot.status === 'available');
      const availableAfternoon = afternoon.filter(slot => slot.status === 'available');

      if (availableMorning.length === 0 && availableAfternoon.length === 0) {
        responseText = `‚ùå No hay horarios disponibles para el ${inputData.displayDate || inputData.requestedDate}\n\nPor favor selecciona otra fecha o escribe *menu* para volver al men√∫ principal.`;
      } else {
        if (inputData.action === 'get_available_times') {
          // Solo consulta de horarios
          responseText = `üìÖ *Horarios disponibles para ${inputData.displayDate}*\n\n`;

          if (availableMorning.length > 0) {
            responseText += `üåÖ **MA√ëANA:**\n`;
            availableMorning.forEach(slot => {
              responseText += `‚Ä¢ ${slot.time}\n`;
            });
            responseText += '\n';
          }

          if (availableAfternoon.length > 0) {
            responseText += `üåÜ **TARDE:**\n`;
            availableAfternoon.forEach(slot => {
              responseText += `‚Ä¢ ${slot.time}\n`;
            });
            responseText += '\n';
          }

          responseText += `üí° Para agendar una cita escribe *1* o *cita*\n\nEscribe *menu* para volver al men√∫ principal.`;
        } else {
          // Para agendar cita - mostrar horarios seleccionables
          responseText = `üìÖ *Horarios disponibles para ${inputData.stateData.displayDate}*\n\n`;
          responseText += `‚úÖ **Selecciona un horario** escribiendo la hora exacta:\n\n`;

          const allAvailable = [];

          if (availableMorning.length > 0) {
            responseText += `üåÖ **MA√ëANA:**\n`;
            availableMorning.forEach(slot => {
              responseText += `‚Ä¢ ${slot.time}\n`;
              allAvailable.push(slot.time);
            });
            responseText += '\n';
          }

          if (availableAfternoon.length > 0) {
            responseText += `üåÜ **TARDE:**\n`;
            availableAfternoon.forEach(slot => {
              responseText += `‚Ä¢ ${slot.time}\n`;
              allAvailable.push(slot.time);
            });
            responseText += '\n';
          }

          responseText += `üìù **Ejemplo:** Escribe *10:00* para seleccionar las 10:00\n\n‚¨ÖÔ∏è O escribe *menu* para cancelar y volver al men√∫ principal.`;

          // Guardar los horarios disponibles en el estado
          updatedStateData.availableTimes = allAvailable;
        }
      }
    } else {
      responseText = `‚ùå Error al consultar horarios disponibles.\n\nPor favor intenta nuevamente o escribe *menu* para volver al men√∫ principal.`;
    }
    break;

  case 'create_appointment':
    if (apiResponse.success && apiResponse.data) {
      const appointment = apiResponse.data;
      responseText = `üéâ **¬°Cita agendada exitosamente!** ‚úÖ\n\n`;
      responseText += `üìã **Resumen de tu cita:**\n\n`;
      responseText += `üë§ **Paciente:** ${appointment.clientName}\n`;
      responseText += `üì± **Tel√©fono:** ${appointment.phone}\n`;
      responseText += `üè• **Obra Social:** ${appointment.socialWork}\n`;
      if (appointment.email) {
        responseText += `üìß **Email:** ${appointment.email}\n`;
      }
      responseText += `üìÖ **Fecha:** ${inputData.stateData.displayDate}\n`;
      responseText += `üïí **Horario:** ${appointment.time}\n\n`;
      responseText += `üìç **Direcci√≥n:** [Tu direcci√≥n del consultorio]\n\n`;
      responseText += `‚ö†Ô∏è **Importante:**\n`;
      responseText += `‚Ä¢ Llega 10 minutos antes\n`;
      responseText += `‚Ä¢ Trae DNI y credencial de obra social\n`;
      responseText += `‚Ä¢ Para cancelar o reprogramar, contacta al consultorio\n\n`;
      responseText += `ü§ñ Escribe *menu* para realizar otra acci√≥n.`;

      // Resetear el estado del usuario
      updatedStateData = {};
    } else {
      responseText = `‚ùå Error al agendar la cita: ${apiResponse.message || 'Error desconocido'}\n\n`;

      if (apiResponse.message && apiResponse.message.includes('no est√° disponible')) {
        responseText += `El horario seleccionado ya fue ocupado por otro paciente.\n\nPor favor selecciona otro horario o escribe *menu* para volver al men√∫ principal.`;
      } else {
        responseText += `Por favor intenta nuevamente o contacta al consultorio.\n\nEscribe *menu* para volver al men√∫ principal.`;
        updatedStateData = {};
      }
    }
    break;

  default:
    responseText = 'Error: Acci√≥n no reconocida';
}

return {
  json: {
    userNumber: inputData.userNumber,
    responseText,
    action: 'send_message',
    updatedStateData
  }
};

// Actualizar estado del usuario despu√©s de respuesta API
const inputData = $input.all()[0].json;

// Actualizar estado si es necesario
if (inputData.updatedStateData && inputData.userNumber) {
  const userStates = $workflow.static.userStates || {};

  if (Object.keys(inputData.updatedStateData).length === 0) {
    // Resetear estado
    userStates[inputData.userNumber] = { state: 'waiting_menu_option', data: {} };
  } else {
    // Actualizar datos del estado
    if (userStates[inputData.userNumber]) {
      userStates[inputData.userNumber].data = { ...userStates[inputData.userNumber].data, ...inputData.updatedStateData };
    }
  }

  $workflow.static.userStates = userStates;
}

return {
  json: {
    userNumber: inputData.userNumber,
    responseText: inputData.responseText
  }
};
