// Intenta parsear la respuesta de la API como JSON.
// Si falla, devuelve un objeto de error amigable.

let raw = $input.item.body || $input.item.text || $input.item.json || '';
let apiResponse;

try {
  if (typeof raw === 'string') {
    apiResponse = JSON.parse(raw);
  } else {
    apiResponse = raw;
  }
} catch (e) {
  apiResponse = {
    success: false,
    message: 'La respuesta de la API no es un JSON v√°lido.',
    raw: raw
  };
}

return {
  json: apiResponse
};
